import React from 'react'
import { NavLink } from 'react-router-dom'
import { LogInLogOutButton } from '../loginLogout/loginLogoutButton'
import { useAuth } from '../../auth'
import {Nav, NavItem, Dropdown, Drop} from 'reactstrap'
import './menu.css'

export function MainMenu() {

  const handleSelect = (eventKey) => alert(`selected ${eventKey}`);

  const authContext = useAuth();
  return (
    // <ul className="main-menu">
    //   <li>
    //     <NavLink exact to="/">Home</NavLink>
    //   </li>

    //   {/* JSX visible for authorised users only */}
    //   {authContext.isAutorised() && <li>
    //     <NavLink exact to="/private">Private Page</NavLink>
    //   </li>}

    //   {/* JSX visible for users, who have role == 'admin'. You can apply any other value here */}
    //   {authContext.hasRole('admin') && <li>
    //     <NavLink exact to="/admin">Admin Page</NavLink>
    //   </li>}
    //   <li className="rihgt-top">
    //     <LogInLogOutButton />
    //   </li>
    // </ul>
    <Nav pills>
    <NavItem>
      <NavLink
        active
        href="#"
      >
        Link
      </NavLink>
    </NavItem>
    <Dropdown
      nav
      toggle={function noRefCheck(){}}
    >
      <DropdownToggle
        caret
        nav
      >
        Dropdown
      </DropdownToggle>
      <DropdownMenu>
        <DropdownItem header>
          Header
        </DropdownItem>
        <DropdownItem disabled>
          Action
        </DropdownItem>
        <DropdownItem>
          Another Action
        </DropdownItem>
        <DropdownItem divider />
        <DropdownItem>
          Another Action
        </DropdownItem>
      </DropdownMenu>
    </Dropdown>
    <NavItem>
      <NavLink href="#">
        Link
      </NavLink>
    </NavItem>
    <NavItem>
      <NavLink href="#">
        Another Link
      </NavLink>
    </NavItem>
    <NavItem>
      <NavLink
        disabled
        href="#"
      >
        Disabled Link
      </NavLink>
    </NavItem>
  </Nav>
  )
}
